/*global google*/
import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Map, GoogleApiWrapper, Marker, InfoWindow, Polyline, DirectionsRenderer, GoogleMapReact } from 'google-maps-react';
import CurrentLocation from './CurrentLocation';

export class MapContainer extends React.Component {
  constructor(props) {
    super(props);
    const { lat, lng } = this.props.initialCenter
    this.state = {
      stores: [],
      showingInfoWindow: false,
      activeMarker: {},
      selectedPlace: {},
      postCode: '',
      distance: 0,
      geocoder: {},
      postCodeCoords: {},
      currentLocation: {
        lat: lat,
        lng: lng
      }
    }
    this.handlePostcodeChange = this.handlePostcodeChange.bind(this);
    this.handleDistanceChange = this.handleDistanceChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });


  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return <Marker key={index} id={index} position={{
       lat: store.lat,
       lng: store.lng
     }}
     onClick={() => console.log("You clicked me!")} />
    })
  }

  getCoordinates = async (postcode) => {
    const locator = new google.maps.Geocoder();
    const coords = await new Promise(function(resolve, reject) {
      locator.geocode({ 'address': postcode }, function(results, status) {
        resolve(results);
      })
    })
    return { lat: coords[0].geometry.location.lat(), lng: coords[0].geometry.location.lng() }
  }

  setCurrentLocation = async () => {
    if (this.props.centerAroundCurrentLocation) {
      if (navigator && navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(pos => {
          const coords = pos.coords;
          this.setState({
            currentLocation: {
              lat: coords.latitude,
              lng: coords.longitude
            }
          });
        });
      }
    }
  }

  setStartPointCoordsFromPostcode = async () => {
    this.getCoordinates(this.state.postCode)
    .then(result => {
      alert('Ready for your ' + this.state.distance + 'km, your postcode is ' + this.state.postCode);
      this.setState({postCodeCoords: result});
    })
  }

  setStartPointCoords = () => {
    if (this.state.postCode == "") {
      console.log("You have no postcode")
    } else {
      this.setStartPointCoordsFromPostcode();
    }
    this.getRoute();
  }

  getRoute = async () => {
    var data = {
      'coordinates': this.state.postCodeCoords,
      'distance': this.state.distance
    }
    console.log(data);l
    fetch('https://routearound-back.herokuapp.com/generate-waypoint-coordinates', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(data)
    })
    .then((response) => {
      return response.json();
    })
    .then((myJson) => {
      this.setState({stores: myJson})
    })
    .catch(error => {
      alert(error)
    })
  }

  handlePostcodeChange(event) {
    this.setState({postCode: event.target.value});
  }

  handleDistanceChange(event) {
    this.setState({distance: event.target.value});
  }

  handleSubmit(event) {
    event.preventDefault()
    this.setStartPointCoords();
    // this.getCoordinates(this.state.postCode)
    // .then(result => {
    //   alert('Ready for your ' + this.state.distance + 'km, your postcode is ' + this.state.postCode);
    //   this.setState({postCodeCoords: result});
    //   var data = {
    //     'coordinates': this.state.postCodeCoords,
    //     'distance': this.state.distance
    //   }
    //   fetch('https://routearound-back.herokuapp.com/generate-waypoint-coordinates', {
    //     method: 'POST',
    //     headers: {
    //       'Content-Type': 'application/json',
    //     },
    //     body: JSON.stringify(data)
    //   })
    //   .then((response) => {
    //     return response.json();
    //   })
    //   .then((myJson) => {
    //     this.setState({stores: myJson})
    //   });
    // })
    // .catch(error => {
    //   alert(error)
    // })
  };

  componentDidMount() {
    this.setCurrentLocation();

    var directionsService = new google.maps.DirectionsService();
    var directionsRenderer = new google.maps.DirectionsRenderer();
    var center = new google.maps.LatLng(this.state.currentLocation.lat, this.state.currentLocation.lng)
    var mapOptions = {
      center: center,
      zoom: 16
    }
    var map = new google.maps.Map(document.getElementById('map'), mapOptions);
    directionsRenderer.setMap(map);
  }

  componentDidUpdate() {
    this.setCurrentLocation();

    var directionsService = new google.maps.DirectionsService();
    var directionsRenderer = new google.maps.DirectionsRenderer({suppressMarkers: true});
    var center = new google.maps.LatLng(this.state.currentLocation.lat, this.state.currentLocation.lng)
    var mapOptions = {
      center: center,
      zoom: 16
    }
    var map = new google.maps.Map(document.getElementById('map'), mapOptions);
    var markerStart = new google.maps.Marker({position: this.state.stores[0]})
    var markerCurrent = new google.maps.Marker({position: this.state.currentLocation})
    markerStart.setMap(map);
    markerCurrent.setMap(map);
    directionsRenderer.setMap(map);

    directionsService.route({
      origin: new google.maps.LatLng(this.state.stores[0]),
      destination: new google.maps.LatLng(this.state.stores[0]),
      waypoints: [
          {location: new google.maps.LatLng(this.state.stores[1])},
          {location: new google.maps.LatLng(this.state.stores[2])},
          {location: new google.maps.LatLng(this.state.stores[3])}
        ],
      avoidHighways: true,
      travelMode: 'WALKING',
      region: 'gb'
    }, function (result, status) {
      directionsRenderer.setDirections(result);
    })
  }


  render() {
    return (
      <div className='App'>
      <div>
        <h1>Route Around</h1>
      <div>
      <form className='App' onSubmit={this.handleSubmit}>
        <label>
          Start Location:
          <input
            name="postCode"
            type="text"
            value={this.state.postCode}
            onChange={this.handlePostcodeChange} />
            (PostCode)
        </label>
        <br />
        <br />
        <label>
          Distance:
          <input
            name="distance"
            type="number"
            value={this.state.distance}
            onChange={this.handleDistanceChange} />
            Kilometres
        </label>
        <br />
        <br />
        <input type="submit" value="GO!" />
        <br />
      </form>
      <div id='map'>
        <CurrentLocation yesIWantToUseGoogleMapApiInternals centerAroundCurrentLocation google={this.props.google}>
          <Marker onClick={this.onMarkerClick} name={'current location'} />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
          <Marker
                icon="https://www.robotwoods.com/dev/misc/bluecircle.png"
                position={this.state.currentLocation}
            />
          {this.displayMarkers()}
        </CurrentLocation>
      </div>
      </div>
      </div>
      </div>
    );
  }
}

// export default App;
export default GoogleApiWrapper({
  apiKey: 'AIzaSyDro0XKEZYd8mj42cXWVukmO0WKJstaAYs&callback='
})(MapContainer);

MapContainer.defaultProps = {
  zoom: 17,
  initialCenter: {
    lat: 51.4934,
    lng: 0.0098
  },
  centerAroundCurrentLocation: true,
  visible: true
};
