import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Map, GoogleApiWrapper, Marker } from 'google-maps-react';

const mapStyles = {
  width: '100%',
  height: '100%',
};

export class MapContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stores: [{latitude: 51.5173391, longitude: -0.0755374}],
      showingInfoWindow: false,
      activeMarker: {},
      selectedPlace: {}
    }
  }

  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return <Marker key={index} id={index} position={{
       lat: store.latitude,
       lng: store.longitude
     }}
     onClick={() => console.log("You clicked me!")} />
    })
  }

  render() {
    return (
      <div>
        <Map
          google={this.props.google}
          zoom={17}
          style={mapStyles}
          initialCenter={{lat: 51.5173391, lng: -0.0755374}}
        >
          {this.displayMarkers()}
        </Map>
      </div>
    );
  }
}

