import React from 'react';
import logo from './logo.svg';
import './App.css';
import { Map, GoogleApiWrapper, Marker, InfoWindow } from 'google-maps-react';
import CurrentLocation from './Map';

export class MapContainer extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      stores: [{latitude: 51.5137188, longitude: -0.0976579}],
      showingInfoWindow: false,
      activeMarker: {},
      selectedPlace: {},
      value: '5km'
    }
  }

  onMarkerClick = (props, marker, e) =>
    this.setState({
      selectedPlace: props,
      activeMarker: marker,
      showingInfoWindow: true
    });


  onClose = props => {
    if (this.state.showingInfoWindow) {
      this.setState({
        showingInfoWindow: false,
        activeMarker: null
      });
    }
  };

  displayMarkers = () => {
    return this.state.stores.map((store, index) => {
      return <Marker key={index} id={index} position={{
       lat: store.latitude,
       lng: store.longitude
     }}
     onClick={() => console.log("You clicked me!")} />
    })
  }

  handleChange(event) {
    this.setState({value: event.target.value});
  }

  handleSubmit(event) {
    alert('Ready for your: ' + this.state.value);
  }

  render() {
    return (
      <div>
        <h1 class='App'>Route Around App</h1>
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Pick Your Distance:
            <select value={this.state.value} onChange={this.handleChange}>
              <option value="5km">5 kilometers</option>
              <option value="10km">10 kilometers</option>
            </select>
          </label>
          <input type="submit" value="Submit" />
        </form>
      <div>
        <CurrentLocation centerAroundCurrentLocation google={this.props.google}>
          <Marker onClick={this.onMarkerClick} name={'current location'} />
          <InfoWindow
            marker={this.state.activeMarker}
            visible={this.state.showingInfoWindow}
            onClose={this.onClose}
          >
            <div>
              <h4>{this.state.selectedPlace.name}</h4>
            </div>
          </InfoWindow>
        </CurrentLocation>
      </div>
      </div>
      </div>
    );
  }
}

// export default App;
export default GoogleApiWrapper({
  apiKey: 'AIzaSyDro0XKEZYd8mj42cXWVukmO0WKJstaAYs'
})(MapContainer);

